pub enum Unpacked {

    /// A file was unpacked.
    File(std::fs::File),

    /// A directory, hardlink, symlink, or other node was unpacked.
    #[doc(hidden)]
    __Nonexhaustive,
}

pub enum EntryType {

    /// Regular file
    Regular,

    /// Hard link
    Link,

    /// Symbolic link
    Symlink,

    /// Character device
    Char,

    /// Block device
    Block,

    /// Directory
    Directory,

    /// Named pipe (fifo)
    Fifo,

    /// Implementation-defined 'high-performance' type, treated as regular file
    Continuous,

    /// GNU extension - long file name
    GNULongName,

    /// GNU extension - long link name (link target)
    GNULongLink,

    /// GNU extension - sparse file
    GNUSparse,

    /// Global extended header
    XGlobalHeader,

    /// Extended Header
    XHeader,

    /// Hints that destructuring should not be exhaustive.
    ///
    /// This enum may grow additional variants, so this makes sure clients
    /// don't count on exhaustive matching. (Otherwise, adding a new variant
    /// could break existing code.)
    #[doc(hidden)]
    __Nonexhaustive(u8),
}

pub enum HeaderMode {

    /// All supported metadata, including mod/access times and ownership will
    /// be included.
    Complete,

    /// Only metadata that is directly relevant to the identity of a file will
    /// be included. In particular, ownership and mod/access times are excluded.
    Deterministic,

    #[doc(hidden)]
    __Nonexhaustive,
}
